<script setup>
import { invoke } from '@tauri-apps/api/tauri'

const image_url = "";

function openImage(){
  console.log("打开图片")
  let that = this;
  invoke('open_image').then(base => {
    console.log(base)
    that.image_url = base;
  })
}
</script>

<template>
  <div>
    <el-button @click="openImage" type="primary">打开文件</el-button>
    <el-image style="width: 100px; height: 100px" :src="image_url"  />
  </div>
</template>

